const API_URL = 'http://localhost:8080/backend/api/';

export const api = {
  registerUser(userData) {
    return fetch(`${API_URL}users/register`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
      body: JSON.stringify(userData),
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errorData) => {
            throw new Error(errorData.message || 'Failed to register user.');
          });
        }
        return response.json();
      });
  },
  loginUser(userData) {
    return fetch(`${API_URL}users/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
      body: JSON.stringify(userData),
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errorData) => {
            throw new Error(errorData.message || 'Failed to log in.');
          });
        }
        return response.json();
      });
  },
  getPersonalPage() {
    const authToken = localStorage.getItem('authToken');
    return fetch(`${API_URL}personal`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errorData) => {
            throw new Error(errorData.message || 'Failed to fetch personal page.');
          });
        }
        return response.json();
      });
  },
  submitPoint(authToken, pointData) {
    return fetch(`${API_URL}points/checkpoint`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${authToken}`,
      },
      body: JSON.stringify(pointData),
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errorData) => {
            throw new Error(errorData.message || 'Failed to submit point.');
          });
        }
        return response.json();
      });
  },
  getPoints(authToken, userId) {
    return fetch(`${API_URL}points/${userId}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${authToken}`,
      },
    })
      .then((response) => {
        if (!response.ok) {
          return response.json().then((errorData) => {
            throw new Error(errorData.message || 'Failed to fetch points.');
          });
        }
        return response.json();
      });
  },
};
